var links_add_goods=document.querySelectorAll(".buy"),add_goods=document.querySelector(".modal-add-goods"),close_add_goods=document.querySelector(".close-add-goods");links_add_goods.forEach(function(d){d.addEventListener("click",function(d){d.preventDefault(),add_goods.classList.add("modal-show")})}),close_add_goods.addEventListener("click",function(d){d.preventDefault(),add_goods.classList.remove("modal-show")});